mapper.select:
SELECT id,
	   name
FROM ucare.user
WHERE id = #{id }
  AND password = #{password }

mapper.update:
UPDATE ucare.user SET name = #{name },
password = #{password },
tel_no = #{telNo }, email = #{email }, address = #{address }, birth = TO_DATE(#{birth },'YYYY-MM-DD'), image = #{image } WHERE id = #{id }

UPDATE  ucare.disease
   SET 
   		disease_nm = #{diseaseNm },
   		symptom = #{symptom }
WHERE disease_no = #{diseaseNo }
